The systemverilog code itself is:
/*
lab1_top.sv

Madeleine Kan
mkan@hmc.edu
3 September, 2025
*/

module lab1_top(
    input logic [3:0] s,
    output logic [2:0] led,
    output logic [6:0] seg
);
    logic clk_new;
    logic [6:0] seg_out;
    // Internal high-speed oscillator
    HSOSC #(.CLKHF_DIV(2'b01)) // 24MHz from clk divider
     hf_osc (.CLKHFPU(1'b1), .CLKHFEN(1'b1), .CLKHF(clk));

    // Counter
    counter count(clk, clk_new);

    // 7-segment display
    seven_segment_display display(s, seg_out);

    // Assign LED output
    assign led[0] = s[0] & s[1];
    assign led[1] = s[2] ^ s[3];
    assign led[2] = clk_new;
    assign seg = seg_out;

endmodule

/*
counter.sv

Takes in clk, clock signal
Outputs clk_new, a new clock signal that rises every 2^23 clk cycles
Madeleine Kan
mkan@hmc.edu
3 September, 2025
*/

module counter
     (input logic clk,
     output logic clk_new);

    logic [23:0] counter;
    always_ff @(posedge clk)
        counter &lt;= counter + 24'b1;
    assign clk_new = counter[23];
endmodule

/*
seven_segment_display.sv
Drives HDSP-521A Seven-Segment Display
with common anode (meaining diodes are
pulled to ground to activate)
Madeleine Kan
mkan@hmc.edu
3 September, 2025
*/

module seven_segment_display(
    input logic [3:0] s,
    output logic [6:0] seg
);
    always_comb
        case(s) 
            //                abcdefg
            4'b0000: seg = 7'b0000001; // 0
            4'b0001: seg = 7'b1001111; // 1
            4'b0010: seg = 7'b0010010; // 2
            4'b0011: seg = 7'b0000110; // 3
            4'b0100: seg = 7'b1001100; // 4
            4'b0101: seg = 7'b0100100; // 5
            4'b0110: seg = 7'b0100000; // 6
            4'b0111: seg = 7'b0001110; // 7
            4'b1000: seg = 7'b0000000; // 8
            4'b1001: seg = 7'b0001100; // 9
            4'b1010: seg = 7'b0001000; // a
            4'b1011: seg = 7'b1100000; // b
            4'b1100: seg = 7'b0110001; // c
            4'b1101: seg = 7'b0000001; // d
            4'b1110: seg = 7'b0110000; // e
            4'b1111: seg = 7'b0111000; // f
            default: seg = 7'b1111111; // none
        endcase
endmodule
